mapper.select:
select s.id summary_id, (s.basis + s.coding + s.thinking + s.express) / 4 average, i.id interview_id, i.title title, i.status status,
from recruitment_batch b left join recruitment_activity a on b.id = a.batch_id and b.is_deleted = 0 and a.is_deleted = 0 left join activity_participation p on p.act_id = a.id and p.is_deleted = 0 and a.is_deleted = 0 left join interview_schedule sc on sc.participation_id = p.id and sc.is_deleted = 0 and p.is_deleted = 0 left join interview i on i.schedule_id = sc.id and i.is_deleted = 0 and sc.is_deleted = 0 left join user stu on stu.id = p.stu_id and stu.is_deleted = 0 and p.is_deleted = 0 left join stu_resume r on r.batch_id = b.id and stu.id = r.user_id and r.is_deleted = 0 and b.is_deleted = 0 and stu.is_deleted = 0 left join interview_summary s on s.interview_id = i.id and s.is_deleted = 0 and i.is_deleted = 0
i.id is not null
and b.id = #{condition.batchId,jdbcType=BIGINT}
and a.id = #{condition.actId,jdbcType=BIGINT}
order by average desc, i.create_time asc
